import React, { useState, useEffect } from 'react';

const BACKEND_URL = 'http://192.168.100.216:5000';

const App: React.FC = () => {
  const [images, setImages] = useState<string[]>([]);
  const [isRunning, setIsRunning] = useState(false);
  const [statusMessage, setStatusMessage] = useState('');

  const startDrone = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/start`, { method: 'POST' });
      if (response.ok) {
        setIsRunning(true);
        setStatusMessage('Drone started');
      } else {
        const data = await response.json();
        setStatusMessage(`Failed to start the drone: ${data.message || 'Unknown error'}`);
      }
    } catch (error) {
      setStatusMessage('Error starting the drone');
    }
  };

  const stopDrone = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/stop`, { method: 'POST' });
      if (response.ok) {
        setIsRunning(false);
        setStatusMessage('Drone stopped');
      } else {
        const data = await response.json();
        setStatusMessage(`Failed to stop the drone: ${data.message || 'Unknown error'}`);
      }
    } catch (error) {
      setStatusMessage('Error stopping the drone');
    }
  };

  const fetchImages = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/images`);
      if (response.ok) {
        const data = await response.json();
        setImages(data.images || []);
      } else {
        setStatusMessage('Failed to fetch images');
      }
    } catch (error) {
      setStatusMessage('Error fetching images');
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div style={{ padding: '50px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f0f8ff', color: '#333', textAlign: 'center' }}>
      <h1 style={{ color: '#007acc', marginBottom: '25px' }}>Underwater Drone Dashboard</h1>
      <div style={{ marginBottom: '25px', minHeight: '25px' }}>{statusMessage}</div>
      <div style={{ marginBottom: '50px' }}>
        <button
          onClick={startDrone}
          disabled={isRunning}
          style={{
            marginRight: '25px',
            backgroundColor: isRunning ? '#a0a0a0' : '#28a745',
            color: 'white',
            border: 'none',
            padding: '25px 50px',
            cursor: isRunning ? 'not-allowed' : 'pointer',
            borderRadius: '15px',
          }}
        >
          Start
        </button>
        <button
          onClick={stopDrone}
          disabled={!isRunning}
          style={{
            backgroundColor: !isRunning ? '#a0a0a0' : '#dc3545',
            color: 'white',
            border: 'none',
            padding: '25px 50px',
            cursor: !isRunning ? 'not-allowed' : 'pointer',
            borderRadius: '15px',
          }}
        >
          Stop
        </button>
      </div>
      <h2 style={{ color: '#007acc', marginBottom: '25px' }}>Latest Images from ESP32-CAM</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '25px', textAlign: 'left' }}>
        {images.length === 0 && <p>No images available</p>}
        {images.slice(0, 4).map((imgUrl, index) => (
          <img
            key={index}
            src={`${BACKEND_URL}/images/${imgUrl}`}
            alt={`Captured ${index}`}
            style={{ width: '100%', height: 'auto', objectFit: 'cover', borderRadius: '15px', border: '1px solid #ccc', transform: 'rotate(-90deg)' }}
          />
        ))}
      </div>
      <button
        onClick={fetchImages}
        style={{
          marginTop: '25px',
          backgroundColor: '#007acc',
          color: 'white',
          border: 'none',
          padding: '25px 50px',
          cursor: 'pointer',
          borderRadius: '15px',
        }}
      >
        Refresh Images
      </button>
    </div>
  );
};

export default App;
